import React, { useState, useContext } from 'react';
import { GlobalContext } from "../context/GlobalState";
import { v4 as uuid } from "uuid";
import { Link, useHistory } from "react-router-dom";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button
} from "reactstrap";

export const AddUser = () => {
  const [name, setName] = useState('');
  const [capacity,setCapacity] = useState('');
  const [status,setStatus] = useState('');
  const [image,setImage] = useState('');
  const { addUser } = useContext(GlobalContext);
  const history = useHistory();

  const onSubmit = (e) => {
    e.preventDefault();
    const newUser = {
      id: uuid(),
      name,
      capacity,
      status,
      image
    }
    addUser(newUser);
    history.push("/");
  }

  const onChange = (e) => {
    setName(e.target.value);
    setCapacity(e.target.value);
    setStatus(e.target.value);
    setImage(e.target.value);
  }

  return (
    <Form onSubmit={onSubmit}>
      <FormGroup>
        <Label>Name</Label>
        <Input type="text" value={name} onChange={onChange} name="name" placeholder="Enter Name" required></Input>
        <Label>Capacity</Label>
        <Input type="number" value={capacity}onChange={onChange}capacity="capacity" placeholder="Enter number of capacity" required></Input>
        <div>
        <Label>Status</Label>
        <Input type="checkbox" value={status} onChange={onChange} status="status" required></Input></div>
        <Label>Image</Label>
        <Input type="file" value={image} onChange={onChange} image="image" accept="image/*" required />
      </FormGroup>
      <Button type="submit">Submit</Button>
      <Link to="/" className="btn btn-danger ml-2">Cancel</Link>
    </Form>
  )
}
