import React, { useState, useContext } from 'react';
import { GlobalContext } from "../context/GlobalState";
import { v4 as uuid } from "uuid";
import { Link, useHistory } from "react-router-dom";
import {
  Form,
  FormGroup,
  Label,
  Input,
  Button
} from "reactstrap";

export const AddUser = () => {
  const [name, setName] = useState('');
  const [capacity, setCapacity] = useState('');
  const [status, setStatus]= useState('');
  const [image, setImage]= useState('');
  const { addUser } = useContext(GlobalContext);
  const history = useHistory();

  const onSubmit = (e) => {
    e.preventDefault();
    const newUser = {
      id: uuid(),
      name,
      capacity,
      status,
      image
    }
    addUser(newUser);
    history.push("/");
  }



  return (
    <Form onSubmit={onSubmit}>
      <FormGroup>
        <Label>Name</Label>
        <Input type="text" value={name} onChange={(e)=>setName(e.target.value)} name="name" placeholder="Enter Name" required></Input>

        <Label>Capacity</Label>
        <Input type="number" value={capacity} onChange={(e)=>setCapacity(e.target.value)}name="capacity" placeholder="Enter Name" required></Input>
       <div style={{}}> 
         <Label><br />status</Label> 
        <Input type="checkbox" value={status} onChange={(e)=>setStatus(e.target.value)}name="status"required></Input><br />
        </div>
        <Label>Image</Label>
        <input type="file" value ={image} onChange={(e)=>setImage(e.target.files[0])}name="image" accept="image/*" />

      </FormGroup>
      <Button type="submit">Submit</Button>
      <Link to="/" className="btn btn-danger ml-2">Cancel</Link>
    </Form>
  )
  }
